function add_yearmonth(df::DataFrame)
    ym = Vector{Int}()
    for i in eachindex(df[:, :dt])
        push!(ym, Int((string(Dates.year(df[i, :dt])) * string(Dates.month(df[i, :dt]))) * "0"^(2 - length(string(Dates.month(df[i, :dt])))))) 
    end
    return hcat(df, DataFrame(ym=ym))
end
