function audi_filter(df_cars)
    filtered_df = filter(row -> row.manufacturer == "audi" .& (row.model .in(["a4", "a4 quattro"])), df_cars)
    new_column = Vector{Bool}(length(filtered_df))
    fill!(new_column, true)

    return hcat(filtered_df, DataFrame(audi_a4_type = new_column))
end
