# Function definition

function audi_filter(df_cars)

  # Create a new DataFrame with the filtered data
  df_audi_a4 = DataFrame()

  # Filter down the dataset by manufacturer and model
  for row in df_cars:
    if row["manufacturer"] == "audi" && row["model"] == "a4" || "a4 quattro":
      df_audi_a4 = bind(df_audi_a4, row)

  # Create a new column with the filtered value
  for row in df_audi_a4:
    if row["manufacturer"] == "audi" && row["model"] == "a4" || "a4 quattro":
      row["audi_a4_type"] = true

  # Return the resulting DataFrame
  return df_audi_a4
end
