def weather_data_analyzer(temps):
    if len(temps) == 0:
        average = max_temp = min_temp = trend = None
    else:
        # Calculate average temperature using mean() function from Statistics package
        average = Statistics.mean(temps)

        # Find maximum and minimum temperatures in the list
        max_temp = max(temps)
        min_temp = min(temps)

        # Determine trend based on the difference between the first and last temperature values
        if temps[0] < temps[-1]:
            trend = ":increasing"
        elif temps[0] > temps[-1]:
            trend = ":decreasing"
        else:
            trend = ":stable"

    # Construct and return a named tuple containing the analysis results
    return (average=average, max_temp=max_temp, min_temp=min_temp, trend=trend)
