using Statistics

struct WeatherDataAnalyzer
@average::Float64
@max::Float64
@min::Float64
@trend::String
end

function weather_data_analyzer(temps)
if isempty(temps)
return WeatherDataAnalyzer(nothing, nothing, nothing, nothing)
else
avg = average(temps)
max = maximum(temps)
min = minimum(temps)

trends = []
for i in 2:length(temps)
if temps[i] > temps[i-1]
push!(trends, "increasing")
else if temps[i] < temps[i-1]
push!(trends, "decreasing")
else
push!(trends, "stable")
end
end

most_common_trend = count_unique_values(trends) %>% max(.5)

return WeatherDataAnalyzer(avg, max, min, most_common_trend)
end
end
