from statistics import mean, median
from collections import namedtuple

WeatherDataAnalyzer = namedtuple("WeatherDataAnalyzer", "average max min trend")

def weather_data_analyzer(temps):
if not temps:
return WeatherDataAnalyzer(*[None] * 4)

avg_temp = mean(temps)
max_temp = max(temps)
min_temp = min(temps)
trend = "stable" if len(set(temps)) == 1 else "increasing" if temps[-1] > temps[0] else "decreasing"

return WeatherDataAnalyzer(avg_temp, max_temp, min_temp, trend)
