from statistics import mean, max, min
from collections.abc import MutableMapping

def weather_data_analyzer(temps: list) -> tuple:
if len(temps) == 0:
return MutableMapping((('average', None), ('max', None), ('min', None), ('trend', 'nothing')))
else:
result = MutableMapping((('average', mean(temps)), ('max', max(temps)), ('min', min(temps))))
if temps == sorted(temps):
result['trend'] = 'increasing'
elif temps == sorted(temps, reverse=True):
result['trend'] = 'decreasing'
else:
result['trend'] = 'stable'
return result
