from statistics import mean, stdev
from collections import namedtuple

def weather_data_analyzer(temps):
if not temps:
return namedtuple('WeatherData', 'nothing average max min trend')._make([None] * 4)

average = mean(temps)
max_temp = max(temps)
min_temp = min(temps)
stdevs = stdev(temps, ddof=1)
trend = ''
if max_temp > average + 2 * stdevs:
trend = 'increasing'
elif max_temp < average - 2 * stdevs:
trend = 'decreasing'
else:
trend = 'stable'

return namedtuple('WeatherData', 'average max min trend')._make([round(x, 1) for x in (temps, average, max_temp, min_temp, trend)])
