from statistics import mean, median
from typing import List, NamedTuple


class WeatherDataAnalyzerResult(NamedTuple):
average: float
max_temp: int
min_temp: int
trend: str


def weather_data_analyzer(temps: List[int]) -> WeatherDataAnalyzerResult:
if not temps:
return WeatherDataAnalyzerResult(average=0, max_temp=0, min_temp=0, trend="nothing")
else:
return WeatherDataAnalyzerResult(
mean=mean(temps),
max_temp=max(temps),
min_temp=min(temps),
trend=(
"increasing" if temps[-1] > temps[0] else "decreasing" if temps[-1] < temps[0] else "stable"
),
)
