struct FloatWithUnits {
value: f64
unit: String
}

impl FloatWithUnits {
fn new(value: f64, unit: &str) -> Self {
FloatWithUnits { value, unit }
}

fn show(&self) -> String {
format!("{} {}", self.value, self.unit)
}
}

fn main() {
let float_with_units = FloatWithUnits::new(1.8, "meters");
println!("{}", float_with_units.show()); // Output: 1.8 meters
}
