import datetime
def event_scheduler(event_list):
    if not event_list:
        return "No events"
    # Create a dictionary to store the events and their start and finish times
    event_dict = {}
    for event in event_list:
        start_time = datetime.datetime.strptime(event[0], '%Y-%m-%d %H:%M')
        end_time = datetime.datetime.strptime(event[1], '%Y-%m-%d %H:%M')
        event_dict[event] = (start_time, end_time)
    # Loop through the events and check for any scheduling conflicts
    for i in range(len(event_list)):
        for j in range(i+1, len(event_list)):
            if event_dict[event_list[i]][0] <= event_dict[event_list[j]][1] and event_dict[event_list[i]][1] >= event_dict[event_list[j]][0]:
                return "Conflict"
    return "No conflicts"
