def event_scheduler(events):
if not events:
return "No events"
for i in range(len(events) - 1):
start_time = events[i][0]
finish_time = events[i][1]
for j in range(i + 1, len(events)):
conflict_start_time = events[j][0]
conflict_finish_time = events[j][1]
if start_time <= conflict_finish_time and finish_time >= conflict_start_time:
return "Conflict"
return "No conflicts"
