def event_scheduler(events):
    if not events:
        return "No events"
    sorted_events = sorted(events, key=lambda x: x[0]) # sort by start time
    conflicts = False
    for i in range(len(sorted_events) - 1):
        if sorted_events[i][1] > sorted_events[i+1][0]:
            conflicts = True
            break # exit loop as soon as conflict is found
    return "No conflicts" if not conflicts else "Conflict"
