from datetime import datetime
from dateutil.relativedelta import relativedelta


def event_scheduler(events):
event_times = []
for start, finish in events:
start = datetime.strptime(start, "%Y-%m-%d %H:%M")
finish = datetime.strptime(finish, "%Y-%m-%d %H:%M")

event_times.append((start, finish))

if len(event_times) == 0:
return "No events"

event_times.sort()
end = event_times[0][1]
conflict = False

for start, finish in event_times[1:]:
if start < end:
conflict = True
break
else:
end = max(start, end)

if conflict:
return "Conflict"
else:
return "No conflicts"
