function extract_julia_code(md)
s = ''

for line in md.lines()
if line.startswith("```") && line.endswith("```")
continue
elsif line.startswith("```julia") && line.endswith("```julia")
continue
else if line.startswith("{{") && line.endswith("}}") || line.startswith("{{") && line.endswith("}}}") || line.startswith("[[") && line.endswith("]]")
push!(s, line.substr(1, -2))
else if line.startswith("```julia") && !line.startswith("{{") && !line.endswith("}}") && !line.endswith("}}") && !line.endswith("[]]")
push!(s, "$$", line.substr(1, -2))
else
push!(s, line)
end
end

return joinlines(s)
end
