class AbstractVehicle(object):
    pass

class Car(AbstractVehicle):
	pass
class Motorcycle(AbstractVehicle):
	pass
class Bus(AbstractVehicle):
	pass
class Truck(AbstractVehicle):
	pass
#%%
def ispersonal(v_type: AbstractVehicle) -> bool:
    return True if v_type in {Car, Motorcycle} else False
assert ispersonal(Bus) == False
assert ispersonal(Truck) == False 
