class AbstractVehicle:
    def __init__(self, make, model):
        self.make = make
        self.model = model

class Car(AbstractVehicle):
    pass

class Motorcycle(AbstractVehicle):
    pass

class Bus(AbstractVehicle):
    pass

class Truck(AbstractVehicle):
    pass

def ispersonal(vehicle_type):
    if vehicle_type == Car:
        return True
    elif vehicle_type == Motorcycle:
        return True
    elif vehicle_type == Bus or vehicle_type == Truck:
        return False
    else:
        return False

# Example usage
my_car = Car('Toyota', 'Camry')
print(ispersonal(my_car)) # Output: True
my_bus = Bus('Greyhound', 'Lancer')
print(ispersonal(my_bus)) # Output: False
