abstract type AbstractVehicle end

immutable Car <: AbstractVehicle end
immutable Motorcycle <: AbstractVehicle end
immutable Bus <: AbstractVehicle end
immutable Truck <: AbstractVehicle end

function ispersonal(veh::AbstractVehicle)
    (veh isa Car || veh isa Motorcycle)
end

# Test cases
@test ispersonal(Car()) == true
@test ispersonal(Motorcycle()) == true
@test ispersonal(Bus()) == false
@test ispersonal(Truck()) == false
