# Pig Latin Transformer

```java
import java.util.*;
import java.lang.*;
import java.io.*;

class Main {

    public static void main(String[] args) throws InterruptedException {

        Scanner scanner = new Scanner(System.in);

        System.out.println("Enter a string to be pig latinified:");
        String input = scanner.nextLine();

        Thread thread1 = new Thread(new Runnable() {
            public void run() {
                System.out.println("String: " + input);

                String[] words = input.split("\\s+");
                ThreadPoolExecutor pool = new ThreadPoolExecutor();

                for (String word : words) {
                    String pigLatinWord = pigLatinify(word);
                    System.out.println("Pig Latin: " + pigLatinWord);

                }
            }
        );

        Thread thread2 = new Thread(new Runnable() {
            public void run() {
                System.out.println("String: " + input);

                String[] words = input.split("\\s+");
                PoolExecutor poolExecutionPool = new PoolExecutionPool();

                for (String word : words) {
                    String pigLatinWord = pigLatinify(word);
                    poolExecutionPool.submit(() -> System.out.println("Pig Latin: " + pigLatinWord));
                }

                PoolWorker worker = new PoolWorker();
                poolExecutionPool.submit(worker);
            }
        );

        thread1.start();
        thread2.start();

        scanner.close();
    }}

// Helper methods to convert a word to pig latin and to print it
static String pigLatinify(String word) {
    String[] vowels = {"a", "e", "i", "o", "u"};

    if (word.startsWith("a") || word.startsWith("e") || word.startsWith("i") || word.startsWith("o") || word.startsWith("u")) {
        return word + "ay";
    } else if (word.endsWith("y")) {
        String lastChar = word.substring(word.length() - 1);
        word = word.substring(0, word.length() - 1) + lastChar.toUpperCase();
        return word + "ay";
    } else {
        int indexOfVowel = 0;

        for (int i = 0; i < word.length(); i++) {
            char c = word.charAt(i);

            if (vowels[indexOfVowel] == c) {
                indexOfVowel++;
            }
        }

        return word.substring(indexOfVowel) + word.substring(0, indexOfVowel) + "ay";
    }}
