def q_and_a_extractor(md):
    # Initialize empty lists to store the question and answer
    questions = []
    answers = []

    # Iterate over each line in the markdown text
    for line in md.splitlines():
        # Check if the current line starts with "# Question" or "# Answer"
        if line.startswith("# Question"):
            # If so, append the previous line (the question) to the questions list
            questions.append(line[9:].strip())
        elif line.startswith("# Answer"):
            # If so, append the current line (the answer) to the answers list
            answers.append(line[7:].strip())

    # Return a tuple of the questions and answers
    return tuple(questions), tuple(answers)
