def q_and_a_extractor(md):
    # Initialize empty lists for questions and answers
    questions = []
    answers = []

    # Split the markdown text into individual lines
    lines = md.splitlines()

    # Iterate through the lines and extract questions and answers
    for line in lines:
        if line.startswith("# Question"):
            questions.append(line[10:].strip())
        elif line.startswith("# Answer"):
            answers.append(line[8:].strip())

    # Return the questions and answers as a tuple
    return (questions, answers)
