def q_and_a_extractor(md):
    # Split the markdown text into individual sections
    sections = md.split("\n\n")

    # Extract the question and answer from each section
    questions, answers = [], []
    for section in sections:
        if section.startswith("# Question"):
            questions.append(section[10:].strip())
        elif section.startswith("# Answer"):
            answers.append(section[8:].strip())

    # Return the question and answer as a tuple
    return (questions[0], answers[0])
