import re

def q_and_a_extractor(md):
    pattern = re.compile(r'^# Question: *(.*)\n.*?^# Answer: *(.*)\n')
    match = pattern.search(md)
    if match:
        question, answer = match.groups()
        return (question.strip(), answer.strip())
    else:
        raise ValueError("No question and answer section found")
