def q_and_a_extractor(md):
    sections = {}
    question_section = None
    answer_section = None

    # Iterate through the lines in the markdown text
    for line in md.split("\n"):
        # Strip leading spaces and newlines
        stripped_line = line.strip()

        # Check if the line starts with a question section
        if stripped_line.startswith("# Question"):
            question_section = stripped_line[1:]
            sections[question_section] = None
            continue

        # Check if the line starts with an answer section
        if stripped_line.startswith("# Answer"):
            answer_section = stripped_line[1:]
            if question_section:
                sections[question_section] = stripped_line[1:]
                break

    # Extract the text from each section and strip leading spaces and newlines
    for key, value in sections.items():
        sections[key] = value.strip() if value else None

    return (sections["# Question"], sections["# Answer"])
