def q_and_a_extractor(md):
    questions = []
    answers = []

    lines = md.split("\n")

    current_section = None

    for line in lines:
        stripped_line = line.strip()

        if stripped_line.startswith("# Question"):
            if current_section is not None:
                answers.append(current_section)
            current_section = []
            questions.append(stripped_line)

        elif stripped_line.startswith("# Answer"):
            current_section = []

        else:
            if current_section is not None:
                current_section.append(stripped_line)

    answers.append(current_section)

    return (questions, "\n".join(answers))


# Test the function with the given data
result = q_and_a_extractor("# Question\nWhat is the capital of France?\n\n# Answer\nThe capital of France is Paris.")
print(result)
