function timezone_bumper(dt::DateTime, bump::Int = 3)
    if Date <: typeof(dt.date)
        throw(DomainError(dt, "The provided timestamp is not a DateTime type"))
    end
    return dt + Duration(bump * Hour)
end

# Example usage
timezone_bumper(DateTime(2023, 4, 10, 12, 0)) == DateTime(2023, 4, 10, 15, 0)  # True
