# Importing necessary libraries
import Data.List (nub)

# Defining the function
function wrap_string(words::AbstractArray{String}, max_text_width::Int)::Array{String} {
var new_line = IntToString(1)
return nub(map(function(word)
if len(word) > max_text_width
return "\n" + word
else 
new_line
end))
}

# Testing the function
words = ["This", "is", "an", "example", "of", "the", "wrap_string", "function"]
max_text_width = 10
result = wrap_string(words, max_text_width)
print(join(" ", result))# Output: This is an\nexample of the wrap\nsring function
