def wrap_string(text: String): List[String] = {
    val words = text.split(" ")
    var lines = List[String]()
    var currentLine = ""
    for (word <- words) {
        if (currentLine.length + word.length > 10) {
            lines += currentLine.trim + "\n"
            currentLine = ""
        }
        currentLine += word + " "
    }
    if (!currentLine.isEmpty) {
        lines += currentLine.trim + "\n"
    }
    lines
}

