def event_scheduler(events):
if not events:
return "No events"
for i in range(len(events) - 1):
for j in range(i + 1, len(events)):
if overlaps(events[i], events[j]):
return "Conflict"
return "No conflicts"
def overlaps(event1, event2):
start1, end1 = parse_date(event1[0]), parse_date(event1[1])
start2, end2 = parse_date(event2[0]), parse_date(event2[1])
return (start1 <= start2 < end1) or (start1 < end2 <= end1)
