def event_scheduler(events):
    if not events:
        return "No events"

    # Sort the events by start time
    events = sorted(events, key=lambda x: x[0])

    # Iterate over the events and check for conflicts
    for i in range(len(events) - 1):
        if events[i][1] > events[i + 1][0]:
            return "Conflict"

    return "No conflicts"
