function isPersonal(vehicle: AbstractVehicle): boolean {
  switch (vehicle) {
    case Car():
      return true;
    case Motorcycle():
      return true;
    case Bus():
      return false;
    case Truck():
      return false;
    default:
      return false;
  }
}
```
Example:
```scss
console.log(isPersonal(Car())); // true
console.log(isPersonal(Motorcycle())); // true
console.log(isPersonal(Bus())); // false
console.log(isPersonal(Truck())); // false
console.log(isPersonal(AbstractVehicle())); // false
```
