def q_and_a_extractor(md):
    question = ""
    answer = ""
    is_q = False
    is_a = False

    for line in md.splitlines():
        if not is_q and not is_a:
            if line == "# Question":
                is_q = True
            elif line == "# Answer":
                is_a = True
            continue

        if is_q:
            if question:
                question += "\n" + line.strip()
            else:
                question = line.strip()

            if line.startswith("# "):
                is_q = False

        elif is_a and answer == "":  # only first non-empty line is considered for the answer
            answer = line.strip()

    return (question,answer)
