def q_and_a_extractor(md):
    question = None
    answer = None
    is_question = False
    is_answer = False

    for line in md.split("\n"):
        if line.startswith("# Question"):
            question = ""
            is_question = True
            is_answer = False
        elif line.startswith("# Answer"):
            answer = ""
            is_answer = True
            is_question = False

        if is_question:
            question += " " + line.replace("# Question", "")
        elif is_answer:
            answer += " " + line.replace("# Answer", "")

    return (question, answer)
