def q_and_a_extractor(md):
    # Initialize variables to store the question and answer
    question = ""
    answer = ""

    # Iterate through each line in the markdown text
    for line in md.splitlines():
        # Check if the current line starts with "# Question"
        if line.startswith("# Question"):
            # If so, set the question to the next line
            question = line[9:].strip()
        # Check if the current line starts with "# Answer"
        elif line.startswith("# Answer"):
            # If so, set the answer to the next line
            answer = line[8:].strip()

    # Return the question and answer as a tuple
    return (question, answer)
