def q_and_a_extractor(md):
    # Split the markdown text into individual sections
    sections = md.split("\n\n")

    # Iterate over the sections and extract the Question and Answer
    for section in sections:
        if section.startswith("# Question"):
            question = section[9:].strip()
        elif section.startswith("# Answer"):
            answer = section[8:].strip()

    # Return the extracted question and answer as a tuple
    return (question, answer)
