def q_and_a_extractor(md):
    # Initialize empty lists for questions and answers
    questions = []
    answers = []

    # Iterate over each line in the markdown text
    for line in md.splitlines():
        # Check if the current line starts with "# Question"
        if line.startswith("# Question"):
            # If so, append the next line to the questions list
            questions.append(next(md.splitlines()))
        # Check if the current line starts with "# Answer"
        elif line.startswith("# Answer"):
            # If so, append the next line to the answers list
            answers.append(next(md.splitlines()))

    # Return a tuple of questions and answers
    return (questions, answers)
