using DataFrames, Test

function test_audi_filter()
    df_cars = DataFrame(
        manufacturer = ["audi", "bmw", "audi", "audi", "audi", "bmw"],
        model = ["a4", "x3", "a4 quattro", "a6", "a4", "x5"]
    )
    result_df = audi_filter(df_cars)
    @test all(result_df.manufacturer .== "audi" && (result_df.model .== "a4" | result_df.model .== "a4 quattro"))
    @test all(result_df.audi_a4_type)
end
