using DataFrames, Test

function test_audi_filter()
    df = DataFrame(
        manufacturer = ["audi", "audi", "bmw", "ford", "audi", "audi"],
        model = ["a4", "a4 quattro", "3-series", "fiesta", "a4", "a6"]
    )
    result = audi_filter(df)
    @test all(result.manufacturer .== "audi" .& (result.model .== "a4" .| result.model .== "a4 quattro"))
    @test all(result.audi_a4_type .== true)
end
@testset begin
    test_audi_filter()
end
