using DataFrames, Test

function test_audi_filter()
    df = DataFrame(
        manufacturer = ["audi", "bmw", "audi", "audi", "audi"],
        model = ["a4", "x5", "a4 quattro", "a4", "a3"]
    )
    result = audi_filter(df)
    @test all(result[(result.manufacturer .== "audi") .& (result.model .== "a4")].audi_a4_type .== true)
    @test all(result[(result.manufacturer .== "audi") .& (result.model .== "a4 quattro")].audi_a4_type .== true)
end
