using DataFrames
using Test

function audi_filter(df_cars::DataFrame)
    filter_rows = filter(row -> (row[:manufacturer] == "audi" && (row[:model] == "a4" || row[:model] == "a4 quattro")), df_cars)
    filter_rows[!, :audi_a4_type] = true
    return filter_rows
end

@testset "audi_filter" begin
    df = DataFrame(
        manufacturer = ["audi", "audi", "bmw", "audi"],
        model = ["a4", "a4 quattro", "x5", "a4 quattro"]
    )

    result = audi_filter(df)

    @test size(result, 1) == 2
    @test all(result[:manufacturer] .== "audi")
    @test all(result[:model] .== ["a4", "a4 quattro"])
    @test all(result[:audi_a4_type] .== true)
end
