function audi_filter(df_cars)
    filtered_audi = df_cars[df_cars.manufacturer .== "audi" .& (df_cars.model .== "a4" | df_cars.model .== "a4 quattro"), :]
    return append!(filtered_audi, DataFrame(audi_a4_type = trues(size(filtered_audi, 1)), copycols = true))
end
