using Dates
using Test

function event_scheduler(events::Vector{Tuple{String, String}})
    if isempty(events)
        return "No events"
    end

    event_intervals = [Dates.value(Dates.DateTime(event[1])):Dates.Minute(1):Dates.value(Dates.DateTime(event[2])) for event in events]

    for i in 1:length(events)
        for j in 1:length(events)
            if i != j && Dates.overlaps(event_intervals[i], event_intervals[j])
                return "Conflict"
            end
        end
    end

    return "No conflicts"
end

@testset begin
    @test event_scheduler([]) == "No events"
    @test event_scheduler([("2023-06-01 12:00", "2023-06-01 13:00"), ("2023-06-01 13:30", "2023-06-01 14:00")]) == "No conflicts"
    @test event_scheduler([("2023-06-01 12:00", "2023-06-01 13:00"), ("2023-06-01 12:30", "2023-06-01 13:30")]) == "Conflict"
end
