using Dates
using Test

function event_scheduler(events)
    if isempty(events)
        return "No events"
    end

    events = sort(events, by=x->Dates.DateTime(x[1], "YYYY-MM-DD HH:MM"))

    for i in 2:length(events)
        if Dates.DateTime(events[i][1], "YYYY-MM-DD HH:MM") <= Dates.DateTime(events[i-1][2], "YYYY-MM-DD HH:MM")
            return "Conflict"
        end
    end

    return "No conflicts"
end

@testset "event_scheduler tests" begin
    @test event_scheduler([]) == "No events"
    @test event_scheduler([("2022-01-01 10:00", "2022-01-01 11:00"), ("2022-01-01 09:30", "2022-01-01 10:30")]) == "Conflict"
    @test event_scheduler([("2022-01-01 10:00", "2022-01-01 11:00"), ("2022-01-01 11:00", "2022-01-01 12:00")]) == "No conflicts"
end
