abstract type AbstractVehicle end

struct Car <: AbstractVehicle end
struct Motorcycle <: AbstractVehicle end
struct Bus <: AbstractVehicle end
struct Truck <: AbstractVehicle end

ispersonal(vehicle::Type{T}) where {T <: AbstractVehicle} = T == Car || T == Motorcycle

# Example
@show ispersonal(Car)    # Output: true
@show ispersonal(Bus)    # Output: false
@show ispersonal(Truck)  # Output: false
@show ispersonal(Int)    # Output: false
```
