function timezone_bumper(dt::DateTime, bump::Integer=3)
    if typeof(dt) == Date
        throw(ErrorException("timezone_bumper can only be applied to DateTime objects, not Date objects. Provide a DateTime value or convert your Date to a DateTime using the `now()` function with a timezone specified if necessary."))
    end
    return DateTime(dt.year, dt.month, dt.day, dt.hour + bump, dt.minute, dt.second)
end
