def extract_julia_code(md):
    blocks = md.split('```')
    result = ""

    # The first block is usually empty due to the string split. Remove it if it's the case
    if len(blocks[0]) == 0:
        blocks = blocks[1:]

    for block in blocks:
        # If a block is not empty, add newline and add it to result
        if block != "":
            result += "\n" + block

    return result.strip()
