function timezone_bumper(dt::DateTime, bump::Integer=3)
    if !isdate(dt)
        throw(ArgumentError("Input must be a DateTime, not just a Date"))
    end
    return DateTime(dt.year, dt.month, dt.day, dt.hour + bump, dt.minute, dt.second, dt.millisecond, tz=dt.timezone)
end
